import React, { useState,useEffect } from "react";
import axios from 'axios'
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

const EditUser = () => {

  let history = useNavigate();
  const {id}=useParams();

  const [user, setUser] = useState({
    name: "",
    enrollment_no: "",
    Class: "",  
    gender: ""
  });

  const { name, enrollment_no, Class, gender } = user;
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    await axios.post("http://localhost:3003/users", user);
    history("/showdata");
  };

  const loadData=async (e)=>{
    const result=await axios.post(`http://localhost:3003/users/${id}`);
    setUser(result.data);
  }

  useEffect(()=>{
   loadData();
  },[])


  return (
    <div className="container">
      <div className="w-75 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Edit User</h2>
        <form onSubmit={e => onSubmit(e)}>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Name"
              name="name"
              value={name}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="number"
              className="form-control form-control-lg"
              placeholder="Enter Your enrollment_no"
              name="enrollment_no"
              value={enrollment_no}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your class Address"
              name="Class"
              value={Class}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your gender Number"
              name="gender"
              value={gender}
              onChange={e => onInputChange(e)}
            />
          </div>
          <button className="btn btn-primary btn-block">Update User</button>
        </form>
      </div>
    </div>
  );
};

export default EditUser;
