import React from 'react'
import {useState,useEffect} from 'react';
import {NavLink} from 'react-router-dom';



function ShowAllData(Data) {
    const [users,setUsers]=useState([]);
    

    const LoadData=async ()=>{
        try{
           const res=await fetch("http://localhost:3003/users");
           const result=await res.json();
        //    console.log(JSON.stringify(result));
           setUsers(result.reverse())
        }
        catch(error)
        {
            console.log(error);
        }
    }

    useEffect(()=>{
     LoadData();
    },[])
  return (
   <>
   <div className='tableDiv'>
   <table>
    <thead>
        <th>Name</th>
        <th>Enrollment no.</th>
        <th>Class</th>
        <th>Gender</th>
    </thead>
    <tbody>
        {
            users.map((current,index)=>{
                return(
                    <>
                    <tr>
                        <th scope="row">{index + 1}</th>
                        <td>{current.name}</td>
                        <td>{current.enrollment_no}</td>
                        <td>{current.class}</td>
                        <td>{current.gender}</td>
                        <td><NavLink to="" className="link">View</NavLink></td>
                        <td><NavLink to="/edituser" className="link">Edit</NavLink></td>
                        <td><NavLink to="" className="link">Delete</NavLink>
                        </td>
                    </tr>
                    </>
                )
            })
        }
    </tbody>
   </table>
   </div>
    </>
  )
}

export default ShowAllData
