import React, { useState } from "react";
import axios from 'axios'
import { useNavigate } from "react-router-dom";

const AddData = () => {
  let history = useNavigate();
  const [user, setUser] = useState({
    name: "",
    enrollment_no: "",
    Class: "",  
    gender: ""
  });

  const { name, enrollment_no, Class, gender } = user;
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const validation=()=>{
    if(!name || !enrollment_no || !Class || !gender)
    {
       alert("Please fill the Userdata");
    }
  }

  const onSubmit = async e => {
    
    e.preventDefault();
    if(!name || !enrollment_no || !Class || !gender)
    {
       alert("Please fill the Userdata");
    }
   
      await axios.post("http://localhost:3003/users", user);
      history("/showdata");
    
  };
  return (
    
      <div className="addDiv">
        <form onSubmit={e => onSubmit(e)}>
        <h2>Add A User</h2>
          <div>
            <label>Name</label>
          </div>
          <div>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your Name"
              name="name"
              value={name}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div>
            <label>Enrollment_no</label>
          </div>
          <div>
            <input
              type="number"
              className="form-control form-control-lg"
              placeholder="Enter Your enrollment_no"
              name="enrollment_no"
              value={enrollment_no}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div>
            <label>Class</label>
          </div>
          <div>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your class Address"
              name="Class"
              value={Class}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div>
            <label>Gender</label>
          </div>
          <div>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Your gender Number"
              name="gender"
              value={gender}
              onChange={e => onInputChange(e)}
            />
          </div>
          <button className="btn btn-primary btn-block">Add User</button>
        </form>
      </div>
    
  );
};

export default AddData;
